INSERT INTO `lark_novel` VALUES ('52', '【性之书吧】【粉丝书评区】【第一届】【大众书评家】活 ..', '\n【活动寄语】&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有这样一群人，他们评论时事、评论文学、评论艺术、评论社会。他们促进着文学、艺术、社会的发展，书吧的发展也需要这样一群人的鞭策。你也许就是那个人。【活动时间】： &nbsp;&nbsp;&nb', '\n【活动寄语】<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有这样一群人，他们评论时事、评论文学、评论艺术、评论社会。他们促进着文学、艺术、社会的发展，书吧的发展也需要这样一群人的鞭策。你也许就是那个人。<br /><br />【活动时间】： <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 宣传时间：10月15日——10月31日 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 比赛时间： 11月1日至11月30日 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />【活动地点】：<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 【粉丝书评区】 <br /><br />【活动要求】： <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、参赛作品按照粉丝书评区格式要求发帖，书评范围包括书吧所有版块。 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、每个ID不限制参赛组和参赛次数限制，但最每个ID只能按回复量多少择优入选一篇作品。 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3、参赛作品必须是原创，即是作者自己构思、码字，而且是首发性吧，否则按伪原创扣50金并回收。参赛主题帖必须符合论坛关于两个“严禁”的精神，否则按有关规定从严处理。 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4、统一标题格式：【大众书评家】【作品名称】【补充说明】【作者ID】 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 【作品名称】由参赛者自选。 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 【补充说明】填写与作品相关的内容，若有副标题，也写在这里面。&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 【作者姓名】请写入参赛者的ID，发帖人和作者ID必须一致。 <br />引用 ', '0');
